import{o,c as a,P as r,d as c,bY as n,l as e,cf as i,k as h,bq as y}from"./index-0c1fde36.js";const f={class:"markdown-body"},p=r('<h2>Syntax</h2><p>Depending on the client used JSONPath expressions do start with <code>$.</code> indicating the root element. Some clients omit the leading <code>$.</code>.</p><table><thead><tr><th>Syntax</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td><code>$.store.book[0].title</code></td><td style="text-align:left;"></td></tr><tr><td><code>store.book[0].title</code></td><td style="text-align:left;">With implicit <code>$.</code></td></tr><tr><td><code>$[&#39;store&#39;][&#39;book&#39;][0][&#39;title&#39;]</code></td><td style="text-align:left;">Alternative notation similar to scripting languages</td></tr></tbody></table><h2>Tree Traversal</h2><table><thead><tr><th>Syntax</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td><code>$.parentNode.childNode.field</code></td><td style="text-align:left;">XPath: <code>/parentNode/childNode/@field</code> (content of “field” of all &quot;childNode&quot;s of “parentNode”)</td></tr><tr><td><code>$..anyChildNode</code></td><td style="text-align:left;">XPath: <code>//anyChildNode</code> (all children at any depth named “anyChildNode”)</td></tr><tr><td><code>$.parentNode.*</code></td><td style="text-align:left;">XPath: <code>/parentNode/*</code> (all children below)</td></tr></tbody></table><h2>Array Access</h2><table><thead><tr><th>Syntax</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td><code>$.myList[0]</code></td><td style="text-align:left;">first element</td></tr><tr><td><code>$.myList[-1]</code></td><td style="text-align:left;">last element</td></tr><tr><td><code>$.myList[2:3]</code></td><td style="text-align:left;">range</td></tr><tr><td><code>$.myList[0,4,5]</code></td><td style="text-align:left;">selection</td></tr></tbody></table><h2>Filtering</h2><table><thead><tr><th>Syntax</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td><code>$.customer[?(@.car)]</code></td><td style="text-align:left;">Only &quot;customer&quot;s that have attribute “car”</td></tr><tr><td><code>$.customer[?(@.car == &#39;Ford Fiesta&#39;)]</code></td><td style="text-align:left;">Only &quot;customer&quot;s with &quot;Ford Fiesta&quot;s</td></tr><tr><td><code>$.customer[?(@.age &gt; 18)]</code></td><td style="text-align:left;">Only adults</td></tr></tbody></table><h2>Complex Conditions</h2><table><thead><tr><th>Syntax</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td><code>$.customer[?(@.age &gt; 18 || @.car == &#39;Ford Fiesta&#39;)]</code></td><td style="text-align:left;">logical or</td></tr><tr><td><code>$.customer[?(@.age &lt; 18 &amp;&amp; @.hobby == &#39;Biking&#39; )]</code></td><td style="text-align:left;">logical and</td></tr></tbody></table><h2>Output Mapping</h2><table><thead><tr><th>Syntax</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td><code>$.[].{Name:name, Age:age, Hobbies:details.hobbies}</code></td><td style="text-align:left;">Mapping fields/nested fields to new set</td></tr></tbody></table><h2>Credits</h2><p>Original author: <a href="https://gist.github.com/mackoj/5786f8b95da0a82e8e003f444c4295bf">https://gist.github.com/mackoj/5786f8b95da0a82e8e003f444c4295bf</a></p>',15),g=[p],b={__name:"jsonpath",setup(l,{expose:t}){return t({frontmatter:{}}),(s,_)=>(o(),a("div",f,g))}},m={"max-w-1200px":""},x=c({__name:"jsonpath-memo",setup(l){n(d=>({"5b99e5f6":e(t).cardColor,"5f3fd958":e(t).textColor1}));const t=i();return(d,s)=>(o(),a("div",m,[h(e(b),{style:{"overflow-x":"auto"}})]))}});const $=y(x,[["__scopeId","data-v-00aeeb8e"]]);export{$ as default};
